#define ll long long
const ll MOD = 1e9+7;

class Solution {
public:
    ll f(int i, int ar1, int ar2, vector<int>&nums,vector<vector<vector<ll>>>&dp){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][ar1][ar2]!=-1){
            return dp[i][ar1][ar2];
        }
        ll cur = 0;
        for(int j = 0 ; j <= nums[i] ; j++){
            if(j>=ar1 && nums[i]-j<=ar2){
                cur += f(i+1,j,nums[i]-j,nums,dp);
                cur %= MOD; 
            }
        }
        return dp[i][ar1][ar2] = cur;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<ll>>>dp(n+1,vector<vector<ll>>(51,vector<ll>(51,-1)));
        int ans = f(0,0,50,nums,dp)%MOD;
        return ans;
    }
};
