class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = int(1e9) + 7
        m = max(nums)
        dp = [[0] * (n + 1) for _ in range(m + 1)]

        up, down = [0], [nums[0]]
        for i in range(1, n):
            for j in range(nums[i] + 1):
                u, d = j, nums[i] - j
                if u >= up[-1] and d <= down[-1]:
                    up.append(u)
                    down.append(d)
                    break
            else:
                return 0

        nums = down

        for i in range(m + 1):
            dp[i][-1] = 1

        for j in range(n - 1, -1, -1):
            for i in range(m, -1, -1):
                if i > nums[j] or dp[i][j+1] == 0:
                    continue
                if i == nums[j]:
                    dp[i][j] = 1
                    continue
                dp[i][j] = dp[i+1][j] + dp[i][j+1]
                dp[i][j] %= MOD

        # for row in dp:
        #     print(row)

        return dp[0][0]
