class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int64_t n = nums.size();
        vector<vector<vector<int64_t>>> num_mps_ending_with(n, vector<vector<int64_t>>(51, vector<int64_t>(51)));
        int64_t MOD = 1000000007;
        for (int i=0; i < n; ++i) {
            if (i == 0) {
                for (int a=0; a <= nums[0]; ++a) {
                    int b = nums[0] - a;
                    num_mps_ending_with[0][a][b] = 1;
                }
            } else {
                for (int a=0; a <= nums[i]; ++a) {
                    int b = nums[i] - a;
                    int64_t ways_before = 0;
                    for (int a_before = 0; a_before <= a; ++a_before) {
                        if (a_before > nums[i-1]) {
                            break;
                        }
                        int b_before = nums[i-1] - a_before;
                        if (b_before < b) {
                            continue;
                        }
                        ways_before += num_mps_ending_with[i-1][a_before][b_before];
                        ways_before %= MOD;
                    }
                    num_mps_ending_with[i][a][b] = ways_before;
                }
            }
        }
        int64_t total = 0;
        for (int i=0; i <= 50; ++i) {
            for (int j=0; j <= 50; ++j) {
                total += num_mps_ending_with[n-1][i][j];
                total %= MOD;
            }
        }
        return total;

    }
};
