#define ll long long int
class Solution {
public:
    ll M=1e9+7;
    ll find(ll i,ll pr,vector<int>&nums,vector<vector<int>>& dp)
    {
        if(i>=nums.size())
        return 1;
        if(dp[i][pr]!=-1)
        return dp[i][pr];
        int ans=0;
        for(int j=0;j<=nums[i];j++)
        {
            if(i==0)
            ans=(ans%M + find(i+1,j,nums,dp)%M)%M;
            else
            {
                if(j>=pr && (nums[i]-j)<=(nums[i-1]-pr))
                ans=(ans%M + find(i+1,j,nums,dp)%M)%M;
            }
        }
        return dp[i][pr]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(52,-1));
        return find(0,51,nums,dp);
    }
};
