class Solution {
public:
    int mod = 1e9 + 7;
    int dp[2005][55];
    int solve(int ind, int pre, vector<int>& nums)
    {
        //cout << ind << " " << pre << endl;
        if(ind >= nums.size()){
            return 1;
        }
        if(dp[ind][pre] != -1) return dp[ind][pre];
        int ans = 0;
        int pr1 = 0, pr2 = 1e9;
        if(ind - 1>= 0){
            pr1 = pre;
            pr2 = nums[ind - 1] - pre;
        }
        for(int i = 0; i <= nums[ind]; i++){
            int c = i;
            int d = nums[ind] - c;
            //cout << ind << " " << c << " " << d << " " << pr1 << " " << pr2 << endl;
            if(c >= pr1 && d <= pr2){
                int ans1 = solve(ind + 1, c, nums) % mod;
                ans = (ans % mod + ans1 % mod) % mod;
                if(ans <= 0){
                    ans = (ans + mod) % mod;
                }
            }
            //cout << "ans " << ans << endl;
        }
        return dp[ind][pre] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(0, 0, nums);
    }
};
