class Solution {
public:
    int MOD = 1e9 + 7;

    int add(long long int a, long long int b) {
        return (a%MOD + b%MOD)%MOD;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<int>> dp(n, vector<int>(51, 0));

        for(int j=0; j<=nums[n-1]; j++) {
            dp[n-1][j] = 1;
        }

        for(int i=n-2; i>=0; i--) {
            // cout<<"Idx "<<i<<endl; 
            for(int j=0; j<=nums[i]; j++) {
                // cout<<j<<endl;
                int st = j;
                int fst = nums[i] - j;
                while(st<=nums[i+1]) {
                    int scnd = nums[i+1] - st;
                    // cout<<"\t"<<st<<" "<<fst<<" "<<scnd<<endl;
                    if(scnd > fst) {
                        st++;
                        continue;
                    }
                    // cout<<"\t\t"<<dp[i+1][st]<<endl;
                    dp[i][j] = add(dp[i][j], dp[i+1][st]);
                    st++;
                }
                // cout<<"\t\t"<<dp[i][j]<<endl;
            }
        }

        int ans = 0;
        for(int j=0; j<=50; j++) {
            ans = add(ans, dp[0][j]);
        }

        return ans;
    }
};
