class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int mod = (int)1e9+7;
        int[][] dp = new int[n][51];
        Arrays.fill(dp[0], 1);
        for(int i = 1; i < n; i++){
            int cur = nums[i];
            int pre = nums[i - 1];
            for(int a = 0; a <= cur; a++){
                for(int prea = 0; prea <= a && pre - prea >= cur - a; prea++){
                    dp[i][a] = (dp[i][a] + dp[i - 1][prea]) % mod;
                }
            }
        }
        int ans = 0;
        for(int i = 0; i <= nums[n - 1]; i++){
            ans = (ans + dp[n - 1][i]) % mod;
        }
        return ans;
    }
}
