/*
n = nums.length
m = max(nums[i])

dp[i][x] := number of pairs in nums[0, i], arr1[i] == x
dp[0][x] = 1, 0 <= x <= nums[0]
dp[i][x] = sum(dp[i-1][y])
y <= x <= nums[i]
nums[i-1] - y >= nums[i] - x >= 0
=> y <= min(x, nums[i-1] - nums[i] + x)
=> TC: O(nmm), SC: O(nm) or O(m)
*/
class Solution {
    public int countOfPairs(int[] nums) {
        final int MOD = (int) Math.pow(10, 9) + 7;
        int n = nums.length;
        int m = 0;
        for (int num : nums) {
            m = Math.max(m, num);
        }
        int[] dp = new int[m + 1];
        for (int x = 0; x <= nums[0]; x++) {
            dp[x] = 1;
        }
        for (int i = 1; i < n; i++) {
            int[] nextDp = new int[m + 1];
            for (int x = 0; x <= nums[i]; x++) {
                for (int y = 0; y <= Math.min(x, nums[i - 1] - nums[i] + x); y++) {
                    nextDp[x] += dp[y];
                    nextDp[x] %= MOD;
                }
            }
            dp = nextDp;
        }
        int ans = 0;
        for (int cnt : dp) {
            ans += cnt;
            ans %= MOD;
        }
        return ans;
    }
}
