class Solution {
    long mod = (long)1e9+7;
    long dp[][];
    long f(int i, int prev, int nums[]){
        if(i == nums.length){
            return 1;
        }
        if(prev != -1 && dp[i][prev] != -1) return dp[i][prev];
        int a1 = prev == -1 ? 0 : prev;
        int a2 = prev == -1 ? 100 : nums[i-1]-prev;
        long cnt = 0;
        for(int j = 0; j <= nums[i]; j++){
            int val1 = j;
            int val2 = nums[i]-j;
            if(a1 <= val1 && a2 >= val2){
                cnt = (cnt +f(i+1, j, nums))%mod;
            }
        }
        return prev == -1 ? cnt : (dp[i][prev] = cnt);
    }
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        dp = new long[n][51];
        for(long it[] : dp){
            Arrays.fill(it, -1);
        }
        return (int)f(0, -1, nums);
    }
}
