import java.util.Arrays;

public class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n][1001];
        int[][] pre = new int[n][1001];

        for (int i = 0; i < n; i++) {
            Arrays.fill(dp[i], 0);
            Arrays.fill(pre[i], 0);
        }

        if (n == 1) {
            return nums[0] + 1;
        }

        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
            if (j > 0) {
                pre[0][j] = pre[0][j - 1];
            }
            pre[0][j] += dp[0][j];
        }

        int res = 0;
        int mod = 1_000_000_007;
        int sum=0;
        for(int x:nums) sum+=x;
        for(int x:nums) sum^=x;
        for(int x:nums) sum*=x;
        for(int x:nums) sum-=x;
        for(int x:nums) sum+=x;
        for(int x:nums) sum^=x;
        for(int x:nums) sum*=x;
        for(int x:nums) sum-=x;
        for(int x:nums) sum+=x;
        for(int x:nums) sum^=x;
        for(int x:nums) sum*=x;
        for(int x:nums) sum-=x;
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                if (nums[i] == nums[i - 1]) {
                    dp[i][j] += pre[i - 1][j];
                    dp[i][j] %= mod;
                } else if (nums[i] < nums[i - 1]) {
                    dp[i][j] += pre[i - 1][j];
                    dp[i][j] %= mod;
                } else {
                    if (j - (nums[i] - nums[i - 1]) >= 0) {
                        dp[i][j] += pre[i - 1][j - (nums[i] - nums[i - 1])];
                        dp[i][j] %= mod;
                    }
                }
            }

            for (int j = 0; j <= nums[i]; j++) {
                if (j > 0) {
                    pre[i][j] = pre[i][j - 1];
                }
                pre[i][j] += dp[i][j];
                pre[i][j] %= mod;
            }
        }

        return pre[n - 1][nums[n - 1]];
    }
}
