using ll = long long;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<ll> vec1(51, 0);
        vector<ll> vec2(51, 0);
        vector<ll> cur_1(51, 0);
        vector<ll> cur_2(51, 0);

        ll MOD = 1e9+7;
        for (int v1 = 0; v1 <= nums[0]; v1 ++) { // set the initial position
            vec1[v1] = 1;
            vec2[nums[0]-v1] = 1; 
        }
        for (int i = 1; i < nums.size(); i ++) {
            // cout << i << ": " << endl;
            for (int v1 = 0; v1 <= nums[i]; v1 ++) {
                int v2 = nums[i] - v1;
                ll mn1 = accumulate(vec1.begin(), vec1.begin() + v1 + 1, 0ll);
                ll mn2 = accumulate(vec2.begin() + v2, vec2.end(), 0ll);
                cur_1[v1] = min(mn1, mn2) % MOD;
                cur_2[v2] = min(mn1, mn2) % MOD;
                // cout << v1 << ": " << cur_1[v1] << " " << v2 << ": " << cur_2[v2] << endl;
            }
            // set to new vec
            vec1 = cur_1;
            vec2 = cur_2;
            // fill impossible
            fill(vec1.begin() + nums[i] + 1, vec1.end(), 0);
            fill(vec2.begin() + nums[i] + 1, vec2.end(), 0);
        }
        ll ret = accumulate(vec1.begin(), vec1.end(), 0ll) % MOD;
        return ret;

    }
    // [40,40,40,41,41,42,42,44,46,49]: 272278100
};
