class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7
        minAhead = [float('inf') for _ in range(n)]
        minimumValue = nums[-1]
        for i in range(n-2, -1, -1):
            minAhead[i] = minimumValue
            minimumValue = min(minimumValue, nums[i])

        prevSplits = defaultdict(int)
        prevSplits[0] = 1
        prevValue = float('inf')

        for i, num in enumerate(nums):
            curSplits = defaultdict(int)
            for prevSplitKey in prevSplits.keys():
                for curSplitKey in range(max(prevSplitKey, num-(prevValue-prevSplitKey)), min(minAhead[i], num) + 1):
                    curSplits[curSplitKey] += prevSplits[prevSplitKey] % MOD
            prevSplits = curSplits
            prevValue = num

        return (sum(prevSplits.values()) % MOD)
