class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        current = {(i, nums[0]-i): 1 for i in range(nums[0]+1)}
        for num in nums[1:]:
            newcurrent = {}
            for small, big in current:
                for newsmall in range(min(small+1, num-big+1)):
                    if (newsmall, num-newsmall) in newcurrent:
                        newcurrent[(newsmall, num-newsmall)] += current[(small, big)]
                    else:
                        newcurrent[(newsmall, num-newsmall)] = current[(small, big)]
            current = newcurrent
        return sum(current.values())%MOD
