#include <vector>
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> memo(n, vector<int>(51, -1));

        function<int(int, int)> countPairs = [&](int i, int prev) {
            if (i == n) return 1;
            if (memo[i][prev] != -1) return memo[i][prev];

            int result = 0;
            for (int arr1 = prev; arr1 <= nums[i]; ++arr1) {
                int arr2 = nums[i] - arr1;
                if (i == 0 || arr2 <= (nums[i-1] - prev)) {
                    result = (result + countPairs(i + 1, arr1)) % MOD;
                }
            }
            return memo[i][prev] = result;
        };

        return countPairs(0, 0);
    }
};
