class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        rows = nums[-1] + 1
        cols = len(nums)
        dp = [[0 for c in range(cols)] for r in range(rows)]
        for r in range(rows):
            dp[r][0] = 1
        for c in range(1, cols):
            diff = max(0, nums[c] - nums[c - 1])
            for r in range(rows):
                for r1 in range(r - diff + 1):
                    dp[r][c] += dp[r1][c - 1]

        ans = 0
        for r in range(rows):
            ans += dp[r][-1]
        return ans % (10**9 + 7)
