#define pii pair<int,pair<int,int>>
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<map<pair<int,int>,int>>mp(n);
        for(int i=0;i<=nums[n-1];i++)
            mp[n-1][{i,nums[n-1]-i}]=1;
        int mod = 1e9+7,ans = 0;
        for(int i=n-2;i>=0;i--){
            for(int j=0;j<=nums[i];j++){
                for(int k=j;k<=nums[i+1];k++){
                    int l = k,h = nums[i+1]-k;
                    if(l<j or h>nums[i]-j)continue;
                    mp[i][{j,nums[i]-j}]=(mp[i][{j,nums[i]-j}]+mp[i+1][{l,h}])%mod;
                }
                //cout<<i<<" "<<j<<" "<<nums[i]-j<<" "<<mp[i][{j,nums[i]-j}]<<endl;
            }
        }
        for(auto x:mp[0]){
            ans = (ans+x.second)%mod;
        }
        return ans;
    }
};
