class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9+7 ;
        vector<vector<long long>> dp(n,vector<long long>(51,0));
        for(int i = 0;i<=nums[0];i++){
            dp[0][i] = 1 ;
        }
        for(int i = 1 ;i<n;i++){
            for(int j = 0 ;j<=nums[i-1];j++){
                for(int k = 0 ;k<=nums[i];k++){
                    if(k<j)
                     continue ;
                    int num1 = nums[i-1]-j;
                    int num2 = nums[i]-k ;
                    if(num2>num1)
                     continue ;
                    dp[i][k] += dp[i-1][j];
                    dp[i][k] %= mod ;
                }
            }
        }
        long long ani = 0 ;
        for(int i = 0 ;i<=nums[n-1];i++){
            ani += dp[n-1][i];
            ani %= mod ;
        }
        int ans = ani ;
        return ans ;
    }
};
