#define ll long long
const ll MOD = (ll)1e9 + 7LL;
class Solution {
public:
    int countOfPairs(vector<int>& A) {
        ll N = A.size();
        vector<ll> dp(55);
        for(ll i = 0; i <= A[0]; ++i) dp[i] = 1;
        for(ll i = 1; i < N; ++i) {
            vector<ll> ndp(55);
            for(ll j = 0; j <= A[i]; ++j) {
                for(ll k = 0; k <= j; ++k) if(A[i - 1] - k >= A[i] - j) {
                    ndp[j] += dp[k];
                    ndp[j] %= MOD;
                }
            }
            swap(dp, ndp);
        }
        ll ans = 0;
        for(auto x : dp)
            ans = (ans + x % MOD) % MOD;
        return ans;
    }
};
