#define LL long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        LL MOD = 1e9+7;
        int n = nums.size();
        vector<vector<LL>> dp(n, vector<LL>(51, 0));

        for(int i=0;i<=50;i++) {
            dp[0][i] = 1;
        }

        for(int i=1;i<n;i++) {
            for(int j=0;j<=nums[i];j++) {
                for(int k=j;k>=0;k--) {
                    if(nums[i]-j <= nums[i-1]-k) {
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= MOD;
                    }
                }
            }
        }

        LL sum = 0;
        for(int i=0;i<=nums[n-1];i++) {
            sum += dp[n-1][i];
            sum %= MOD;
        }

        return sum;
    }
};

