class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [0] * 51
        num = nums[0]
        for x in range(num+1):
            dp[x] = 1

        prev_num = nums[0]
        for num in nums[1:]:
            dp_new = [0] * 51
            for x in range(num+1):
                y = num - x
                for i in range(x+1):
                    j = prev_num - i
                    if x >= i and y <= j:
                        dp_new[x] += dp[i]

            dp = dp_new
            prev_num = num

        const = 10 ** 9 + 7
        return sum(dp) % const
