class Solution {
    int m = 1000000007;
    int n;
    int[] num;
    int[][] dp;
    boolean[][] vis;
    public int countOfPairs(int[] nums) {
        num=nums;
        n=num.length;
        dp=new int[n][51];
        vis=new boolean[n][51];
        int ans = 0;
        for (int i=0; i<=nums[0]; i++) {
            ans+=f(0,i);
            ans%=m;
        }
        return ans;
    }
    public int f(int idx, int val) {
        if (idx==n-1) return 1;
        if (vis[idx][val]) return dp[idx][val];
        vis[idx][val]=true;

        int val2 = num[idx]-val;
        int lo = Math.max(val,num[idx+1]-num[idx]+val);
        for (int i=lo; i<=num[idx+1]; i++) {
            dp[idx][val]+=f(idx+1, i);
            dp[idx][val]%=m;
        }
        return dp[idx][val];
    }
}
