#define ll long long
const int M = 1e9 + 7;
ll dp[2002][1001];
class Solution {
public:
    ll func(int i, int last, vector<int> &nums){
        if(i == nums.size())return 1;
        int p1, p2;
        if(dp[i][last] != -1)return dp[i][last];
        if(i == 0){
            ll ans = 0;
            for(int j=0;j<=nums[i];j++){
                ans = (ans + func(i + 1, j, nums)) % M;
            }
            return dp[i][last] = ans;
        }
        p1 = last;
        p2 = nums[i - 1] - last;
        ll ans = 0;
        for(int j=last;j<=nums[i];j++){
            if(nums[i] - j <= p2){
                ans = (ans + func(i + 1, j, nums)) % M;
            }
        }
        return dp[i][last] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        for(int i=0;i<=nums.size();i++){
            for(int j=0;j<=1000;j++){
                dp[i][j] = -1;
            }
        }
        return func(0, 0, nums);
    }
};
