#define ll long long
ll M=1e9+7;
class Solution {
public:
    ll func(int ind,int l1,int l2,vector<int>&a,vector<vector<vector<ll>>>&dp){
        int n=a.size();
        if(ind>=n) return 1;
        if(dp[ind][l1][l2]!=-1) return dp[ind][l1][l2];
        ll ans=0;
        for(int i=l1; i<=a[ind]; i++){
            int j=a[ind]-i;
            if(j<=l2) ans=(ans+func(ind+1,i,j,a,dp))%M;
        }
        return dp[ind][l1][l2]=ans;
    }
    int countOfPairs(vector<int>& a) {
        int n=a.size();
        vector<vector<vector<ll>>> dp(n,vector<vector<ll>>(55,vector<ll>(55,-1)));
        ll ans=func(0,0,50,a,dp);
        return ans;
    }
};
