class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
        int n=nums.size(),m=1e9+7;
        vector<vector<int>>dp(51,vector<int>(51,0));
        for(int v=0;v<=nums[0];v++)
        {
            dp[v][nums[0]-v]=1;
        }
        for(int i=1;i<n;i++)
        {
            vector<vector<int>>temp(51,vector<int>(51,0));
           for(int vc=0;vc<=nums[i];vc++)
           {
               for(int v=0;v<=nums[i-1];v++)
               {
                  if(vc<=v && nums[i]-vc>=nums[i-1]-v)
                  {
                      //cout<<vc<<" "<<nums[i]-vc<<" "<<v<<" "<<nums[i-1]-v<<"\n";
                     temp[vc][nums[i]-vc]=(temp[vc][nums[i]-vc]+dp[v][nums[i-1]-v])%m;
                  }
               }
           }
           dp=temp;
        }
        int ans=0;
        for(int v=0;v<=nums[n-1];v++)
        {
            ans=(ans+dp[v][nums[n-1]-v])%m;
        }
        return ans;
    }
};
