#define ll long long

class Solution {
public:
    const ll MOD = 1e9 + 7;
    const int MAXK = 51;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<ll>> dp(n, vector<ll>(MAXK, 0));

        for (int k = 0; k <= nums[0]; k++) {
            dp[0][k] = 1;
        }

        for (int i = 1; i < n; i++) {
            for (int k = 0; k <= nums[i]; k++) {
                for (int ki = 0; ki <= nums[i-1]; ki++) {
                    if (k >= ki && (nums[i] - k <= nums[i-1] - ki)) {
                        dp[i][k] = (dp[i][k] + dp[i-1][ki]) % MOD;
                    }
                }
            }
        }

        ll total = 0;
        for (int i = 0; i < MAXK; i++) {
            total = (total + dp[n-1][i]) % MOD;
        }

        return (int) total;
    }
};
