const int mod = 1000 * 1000 * 1000 + 7;
const int maxn= 2500;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<int> d[2];
        d[0].resize(maxn);
        d[1].resize(maxn);
        int t = 0;

        for(int q = 0; q <= nums[0]; ++q) {
            d[0][q] = 1;
        }

        for(int i = 1; i < n; ++i) {
            t = 1 - t;
            d[t].assign(maxn, 0);

            for(int p = 0; p <= nums[i]; ++p) {
                for(int q = 0; q <= min(nums[i-1], min(p, p - nums[i] + nums[i-1])); ++q) {
                    //cout << t << " " << p << " " << q << endl;
                    d[t][p] = (d[t][p] + d[1-t][q]) % mod;
                }
            }
        }

        int res = 0;
        for (int i = 0; i < maxn; ++i) {
            res = (res + d[t][i]) % mod;
        }

        return res;
    }
};
