class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long mod = 1E9 + 7;
        long long n = nums.size();
        // [index][arr1 value]
        vector<vector<long long>> v(n, vector<long long>(51, 0));
        for (long long i = 0; i <= nums[0]; i++) {
            v[0][i]++;
        }
        for (long long i = 1; i < n; i++) {
            for (long long a1 = 0; a1 <= nums[i]; a1++) {
                // calc v[i][a1];
                long long a2 = nums[i] - a1;
                for (long long preva1 = 0; preva1 <= nums[i - 1]; preva1++) {
                    long long preva2 = nums[i - 1] - preva1;
                    // validate
                    if (preva1 > a1 || preva2 < a2) {
                        continue;
                    }
                    v[i][a1] += v[i - 1][preva1];
                    v[i][a1] %= mod;
                }
            }
        }

        // for (auto row : v) {
        //     for (auto k : row) {
        //         cout << k << " ";
        //     }
        //     cout << endl;
        // }

        return accumulate(v[n - 1].begin(), v[n - 1].end(), 0LL) % mod;
