class Solution {
public:
    static constexpr int mod = 1e9+7;
    int add(int x,int y){
        if((x+=y)>=mod)x-=mod;
        return x;
    }
    int countOfPairs(vector<int>& nums) {
        const int n = nums.size();
        vector<vector<int>> dp(n,vector<int>(51,0));
        for(int i=0;i<=nums[0];++i) dp[0][i] = 1;

        for(int i=1;i<n;++i){
            for(int j=0;j<=nums[i];++j){
                for(int k=0;k<=min(j,nums[i-1]);++k){
                    if(j-k >= max(0, nums[i] - nums[i-1])){
                        dp[i][j] = add(dp[i][j], dp[i-1][k]);
                    }
                }
            }
        }
        int ret = 0 ;
        for(int i=0;i<=50;++i) ret = add(ret, dp[n-1][i]);
        return ret;
    }
};
