class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [1]*(nums[0]+1)
        module = 1000000007
        for pre_num, num in zip(nums, nums[1:]):
            _dp = [0]*(num+1)
            for pre1, cnt in enumerate(dp):
                pre2 = pre_num-pre1
                # print(pre1)
                for num1 in range(pre1, num+1):
                    num2 = num-num1
                    if num2>pre2:
                        continue
                    _dp[num1] = (_dp[num1]+cnt)%module
            dp = _dp
            # print(dp)
        return sum(dp)%module
