class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int count[51][2001] = {0};
        for (int i = 0; i < 51; ++i) {
            count[i][0] = 1;
        }

        int mod = 1e9 + 7;
        for (int i = 1; i < nums.size(); ++i) {
            for (int a = 0, b = nums[i]; b >= 0; ++a, --b) {
                for (int la = 0, lb = nums[i - 1]; la <= a; ++la, --lb) {
                    if (lb >= b) {
                        count[a][i] = (count[la][i - 1] + count[a][i]) % mod;
                    }
                }
            }
        }


        int res = 0;
        for (int i = 0; i <= nums.back(); ++i) {
            res = (res + count[i][nums.size() - 1]) % mod;
        }
        return res;
    }
};
