class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(),i,j,k,mod=1e9+7;
        vector<vector<long long>> dp(n,vector<long long>(51,0));
        for(i=0;i<=nums[0];i++){
                dp[0][i]=1;
        }
        for(i=1;i<n;i++){
            int x=nums[i-1],y=nums[i];
            for(j=0;j<=x;j++){
                for(k=j;k<=y;k++){
                    if(y-k<=x-j){
                        dp[i][k]=(dp[i][k]+dp[i-1][j])%mod;
                    }
                }
            }
        }
        long long ans=0;
        for(i=0;i<51;i++){
            ans=(ans+dp[n-1][i])%mod;
        }
        // for(i=0;i<n;i++){
        //     for(j=0;j<51;j++){
        //         cout<<dp[i][j]<<" ";
        //     }
        //     cout<<"\n";
        // }
        // cout<<"\n";
        return ans;
    }
};
