class Solution {
public:
    long dp[2001][51];
    int mod = 1000000007;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        for(int j=0; j<=nums[0]; j++) dp[0][j]=1;

        for(int i=1; i<n; i++){
            for(int j=0; j<=nums[i]; j++){
                for(int prev=0; prev<=j; prev++){
                    if(nums[i-1]-prev >= nums[i]-j) {
                        dp[i][j] += dp[i-1][prev];
                        dp[i][j] %= mod;
                    }
                }
            }
        }

        long ans = 0;
        for(int j=0; j<=nums[n-1]; j++){
            ans += dp[n-1][j];
            ans %= mod;
        }
        return int(ans);
    }
};
