class Solution {
public:
#define MXN 2005
#define MXC 55
#define MOD 1'000'000'007
    int dp[MXN][MXC];
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        nums.insert(nums.begin(), 2000);
        dp[0][0] = 1;
        for(int i = 1; i <= n; i++){
            for(int j = 0 ; j <= nums[i]; j++){
                for(int k = 0; k <= min(nums[i-1], j); k++){
                    if(nums[i-1]-k >= nums[i]-j){
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= MOD;
                    }
                }
            }
        }
        int ret = 0;
        for(int i = 0; i < MXC; i++){
            ret += dp[n][i];
            ret %= MOD;
        }
        return ret;
    }
};
