class Solution {
public:

    vector<vector<vector<int>>> dp;
    const int MOD = 1e9 + 7;

    int helper(vector<int>& nums, int i, int prevMin, int prevMax) {
        int n = nums.size();

        if(i == n) 
            return 1;

        if(dp[i][prevMin][prevMax] != -1) return dp[i][prevMin][prevMax];

        int res = 0;
        int curr = nums[i];
        for(int val1 = prevMin; val1 <= curr; val1++) {
            int val2 = curr - val1;
            // cout << val1 << " " << val2 << endl;
            // cout << prevMin << " " << prevMax << endl;
            if(prevMin <= val1 && prevMax >= val2) {
                res += helper(nums, i+1, val1, val2);
                res %= MOD;
            } 
            // cout << "returned" << endl;

        }

        return dp[i][prevMin][prevMax] = res;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int sz = 51;
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(sz, vector<int>(sz, -1)));
        return helper(nums, 0, 0, 50);
    }
};
