class Solution(object):
    def countOfPairs(self, nums):
        if len(nums) == 1:
            return nums[0] + 1



        dp = [[0 for i in range(51)] for x in range(len(nums))]

        for i in range(51):
            dp[-1][i] = 1

        mod = 10 ** 9 + 7

        for i in range(len(nums) - 2, -1, -1):
            for v in range(nums[i] + 1):
                for v2 in range(nums[i + 1] + 1):
                    if nums[i + 1] - v2 >= nums[i] - v and v >= v2:
                        dp[i][v] += dp[i + 1][v2]
                        dp[i][v] %= mod

        return sum(dp[0]) % mod
