class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = int(10**9) + 7
        cap = [nums[-1]]*len(nums)
        for i in range(len(nums)-2, -1, -1):
            cap[i] = min(nums[i], cap[i+1])
        @cache
        def dp(i, prev1, prev2):
            if i == len(nums):
                return 1
            if nums[i] < prev1:
                return 0
            res = 0
            for x in range(prev1, cap[i]+1):
                y = nums[i] - x
                if y > prev2:
                    continue
                res += dp(i+1, x, y)
                res %= mod
            return res
        ret = dp(0, 0, float('inf'))
        dp.cache_clear()
        return ret
