class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int max_val = *max_element(nums.begin(), nums.end());
        int n = nums.size();
        vector<vector<long>> dp(n, vector<long>(max_val + 1, 0));
        for(int val = 0; val <= max_val; ++val){
            dp[0][val] = 1;
        }

        int mod = 1e9 + 7;
        for(int i = 1; i < n; ++i){
            for(int val = 0; val <= nums[i]; ++val){
                // x1 = val, x2 = max_val - val;
                for(int k = 0; k <= nums[i - 1]; ++k){
                    // prev_x1 = k, prev_x2 = max_val - k
                    if(val >= k && (nums[i] - val) <= (nums[i - 1] - k)){
                        dp[i][val] += dp[i - 1][k];
                        dp[i][val] %= mod;
                    }
                }
            }
        }

        long res = accumulate(dp[n - 1].begin(), dp[n - 1].end(), 0LL);
        res %= mod;

        return res;
    }
};
