class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[0 for i in range(max(nums)+1)] for j in range(n)]
        MOD = 1000_000_000 + 7
        for i in range(nums[0]+1):
            dp[0][i] = 1
        for i in range(1,n):
            for k in range(nums[i]+1):
                a, b = k, nums[i] - k
                for l in range(nums[i-1]+1):
                    c, d = l, nums[i-1] - l 
                    if b>=0 and d>=0 and a>=c and d>=b:
                        dp[i][k] = (dp[i][k] + dp[i-1][l]) % MOD
        return sum(dp[n-1])%MOD
