class Solution {
public:
    int dp[2001][100];
    int mod=1e9+7;
    int helper(int i, vector<int> & arr, int pre_val){


        if(i==arr.size()){
            return 1;
        }

        int limit1=pre_val;
        int limit2=arr[i];
        int ans=0;

        if(i!=0){
            limit2=arr[i-1]-pre_val;
        }

        if(dp[i][pre_val] !=-1){
            return dp[i][pre_val];
        }

        for(int k= limit1; k<=arr[i]; k++){

            int val2= arr[i]-k;

            if(val2<=limit2 && val2>=0){
                ans+= helper(i+1, arr, k);
                ans%=mod;
            }
        }
        return dp[i][pre_val]=ans;
    }
    int countOfPairs(vector<int>& nums) {

        for(int i=0; i<nums.size();i++){

            for(int j=0; j<=50;j++){
                dp[i][j]=-1;
            }
        }
        return helper(0, nums, 0);
    }
};
