constexpr int mod = 1e9 + 7;
const int M = 50;
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        const int n = a.size();
        array<int64_t, M + 1> d{};
        fill(d.begin(), d.begin() + a[0] + 1, 1);

        for (int i = 1; i < n; ++i) {
            array<int64_t, M + 1> nd{};
            for (int j = 0, k = a[i-1]; k >= 0; ++j, --k) {
                for (int t = 0; t <= a[i]; ++t) {
                    if (t >= j && (a[i] - t) <= k) {
                        nd[t] += d[j];
                        nd[t] %= mod;
                    }
                }
            }
            d = nd;
        }
        int64_t ans = 0;
        for (auto x : d) ans += x;
        return ans % mod;
    }
};
