class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long MOD = 1e9+7;
        int n = nums.size();
        vector<vector<long long>> dp(n);
        for(int i=0; i<n; i++) for(int j=0; j<51; j++) dp[i].push_back(0);

        // dp[i][j]
        // no. of valid answer until nums[i]
        // j is the value of ascending array at point nums[i]

        // initial condition
        for(int j=0; j<51; j++){
            int a = j;
            int d = nums[0]-a;
            if((a>=0) && (d>=0)) dp[0][j]++;
        }

        // loop
        for(int i=1; i<n; i++){
            for(int j=0; j<51; j++){
                for(int k=0; k<51; k++){
                    int a = j;
                    int d = nums[i]-a;
                    int a1 = k;
                    int d1 = nums[i-1]-a1;

                    // all should be +ve
                    if((d<0) || (d1<0)) continue;

                    // monotonicity
                    if(a < a1) continue;
                    if(d > d1) continue;

                    // add if everyting is met
                    dp[i][j] = (dp[i][j] + dp[i-1][k])%MOD;
                }
            }
        }

        long long ans = 0;
        // final sum
        for(int j=0; j<51; j++) ans = (ans + dp[n-1][j])%MOD;

        return ans;
    }
};
