class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10 ** 9 + 7
        dp_cur = collections.defaultdict(int)
        for i in range(nums[0] + 1):
            dp_cur[(i, nums[0] - i)] += 1

        for j in range(1, n):
            dp_nxt = collections.defaultdict(int)
            for i in range(nums[j] + 1):
                for (front, end), val in dp_cur.items():
                    if i >= front and (nums[j] - i) <= end:
                        dp_nxt[(i, nums[j] - i)] += val
                        dp_nxt[(i, nums[j] - i)] %= MOD
            dp_cur = dp_nxt

        ans = 0
        for (front, end), val in dp_cur.items():
            ans += val
            ans %= MOD
        return ans
