func countOfPairs(nums []int) int {
    n := len(nums)
    const M int = 1e9+7
    mx := slices.Max(nums)

    dp := make([][]int, n)
    for i := range dp {
        dp[i] = make([]int, mx+1)
    }

    for i:=0; i<=nums[0]; i++ {
        dp[0][i] = 1
    }


    for i:=1; i<n; i++ {

        for a:=0; a<=nums[i-1]; a++ {
            preb := nums[i-1]-a 
            for a1:=a; a1<=nums[i]; a1++ {
                curb := nums[i]-a1
                if curb <= preb {
                    dp[i][a1] += dp[i-1][a]
                    dp[i][a1] %= M
                }
            }
        }
    }

    res := 0
    for i:=0; i<=nums[n-1]; i++ {
       res += dp[n-1][i]
        res %= M
    }
    return res
}
