class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[0]*51 for _ in range(n)]
        for i in range(nums[0]+1): dp[0][i]=1

        for i in range(1, n):
            for j in range(nums[i]+1):
                for step in range(max(0, 1+min(j, nums[i-1]-nums[i]+j))):
                    dp[i][j] = (dp[i][j]+dp[i-1][step])%(int(1e9)+7)
        # print(dp)
        return sum([dp[n-1][i] for i in range(51)])%(int(1e9)+7)
