class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1000000007;

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));

        for (int num1 = 0; num1 <= nums[0]; ++num1) {
            int num2 = nums[0] - num1;
            dp[0][num1][num2] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int num1 = 0; num1 <= nums[i]; ++num1) {
                int num2 = nums[i] - num1;

                for (int prev_num1 = 0; prev_num1 <= num1; ++prev_num1) {
                    int prev_num2 = nums[i - 1] - prev_num1;
                    if (prev_num2 >= num2) {
                        dp[i][num1][num2] += dp[i - 1][prev_num1][prev_num2];
                        dp[i][num1][num2] %= mod;
                    }
                }
            }
        }

        int result = 0;
        for (int num1 = 0; num1 <= nums[n - 1]; ++num1) {
            int num2 = nums[n - 1] - num1;
            result += dp[n - 1][num1][num2];
            result %= mod;
        }

        return result;
    }
};
