class Solution {

public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int mod=1e9+7;
        int dp[n][51];
        memset(dp,0,sizeof(dp));
        for(int i=0;i<n;i++){
            int val=nums[i];
            for(int j=0;j<=val;j++){
                int cur1=j,cur2=(val-j);
                if(i>0){
                    for(int k=0;k<=nums[i-1];k++){
                        int prev1=k,prev2=(nums[i-1]-k);
                        if(cur1>=prev1 and cur2<=prev2){
                            dp[i][j]+=dp[i-1][k];
                            dp[i][j]%=mod;
                        }
                    }
                }
                else{
                    dp[i][j]=1;
                }
            }
        }
        int ans=0;
        for(int i=0;i<=50;i++) {ans+=(dp[n-1][i]);ans%=mod;}
        return ans;
    }
};
