class Solution {

    int MOD = (int)1e9 + 7;

    public int mod(long a, long b){
        return (int)((a + b) % MOD);
    }

    public int dp(int i, int p, int[][] dp, int[] nums){
        int n = nums.length;

        if(i >= n) return 1;
        if(dp[i][p] != -1) return dp[i][p];

        int count = 0;

        for(int j = p ; j <= nums[i] ; j++){
            int a = j;
            int b = nums[i]-j;

            if(a >= p && (i == 0 || b <= nums[i-1]-p)){
                count = mod(dp(i+1, a, dp, nums), count);
            }
        }

        return dp[i][p] = count;
    }

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n][51];

        for(int[] a : dp){
            Arrays.fill(a, -1);
        }

        return dp(0, 0, dp, nums);
    }
}
