#define mo 1000000007

int d[2][1100];
int sum[1100];

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int i,j,k,now,ans,x,y,ll,rr,ll1,rr1;
        memset(d[0],0,sizeof(d[0]));
        for (i=0;i<=a[0];i++)
            d[0][i]++;
        now=0;
        for (i=1;i<a.size();i++)
        {
            sum[0]=0;
            for (j=0;j<=1000;j++)
            {
                sum[j+1]=sum[j]+d[now][j];
                sum[j+1]%=mo;
            }
            memset(d[1-now],0,sizeof(d[1-now]));
            for (j=0;j<=a[i];j++)
            {
                x=j;
                y=a[i]-j;
                ll=0;rr=min(x,a[i-1]-y);
                if (rr>=ll)
                {
                    d[1-now][j]=sum[rr+1]-sum[ll];
                    if (d[1-now][j]<0) d[1-now][j]+=mo;
                }
            }

            now=1-now;
        }
        ans=0;
        for (i=0;i<=a.back();i++)
        {
            ans+=d[now][i];
            ans%=mo;
        }
        return ans;
    }
};
