class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dfs(index, arr1, arr2):
            if index == n:
                return 1
            res = 0
            for new in range(max(arr1, nums[index] - arr2), nums[index] + 1):
                res = (res + dfs(index + 1, new, nums[index] - new)) % mod
            return res

        mod, n = pow(10, 9) + 7, len(nums)
        return sum(dfs(1, i, nums[0] - i) for i in range(nums[0] + 1)) % mod
