class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def helper(i, a1, a2):
            if i == len(nums):
                return 1
            res = 0
            for j in range(a1, nums[i] + 1):
                if nums[i] - j > a2:
                    continue
                res += helper(i + 1, j, nums[i] - j)
            return res % 1_000_000_007

        res = helper(0, 0, nums[0])
        helper.cache_clear()
        return res


